mapper.select:
SELECT id, build_status, build_start_time, build_end_time FROM application_build WHERE id IN
#{id}
AND deleted = 1

SELECT branch_name
FROM application_build
WHERE repo_id = #{repoId}

    AND app_id = #{appId}


    AND profile_id = #{profileId}

AND branch_name IS NOT NULL
AND deleted = 1
ORDER BY id DESC
LIMIT 1

SELECT id, build_seq, description, create_time
FROM application_build
WHERE app_id = #{appId}
AND profile_id = #{profileId}
AND build_status = 30
AND deleted = 1
ORDER by id DESC
LIMIT #{limit}

SELECT id, app_id, FROM application_build WHERE app_id IN
#{appId}
AND profile_id = #{profileId} AND build_status = 30 AND deleted = 1 GROUP BY app_id ORDER by id DESC

SELECT
    COUNT(1) build_count,
    COUNT(build_status = 30 OR NULL) success_count,
    COUNT(build_status = 40 OR NULL) failure_count,
    IFNULL(
        FLOOR(
            AVG(
                IF(
                    build_status = 30
                    AND build_end_time IS NOT NULL
                    AND build_start_time IS NOT NULL,
                    UNIX_TIMESTAMP(build_end_time) * 1000 - UNIX_TIMESTAMP(build_start_time) * 1000,
                    NULL
                )
            )
        ), 0
    ) avg_used
FROM application_build
WHERE deleted = 1
AND app_id = #{appId}
AND profile_id = #{profileId}

    AND create_time >= #{rangeStartDate}
